import React from "react"
import io from "socket.io-client"
import {Paper, Button, TextField, ExpansionPanel, ExpansionPanelSummary, Typography, ExpansionPanelDetails} from '@material-ui/core'
import axios from 'axios'
// import ExpandMoreIcon from '@material-ui/icons/ExpandMoreIcon';

import './Chat.css'

class Chat extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            expanded: null,
            id: [],
            recipient: '',
            message: '',
            messages: []
        };

        this.socket = io('localhost:8080');

        // this.socket.on('connected', function(url) {
        //     addUserNames(url)
        // })

        this.socket.on('disconnected', function(data) {
            removeClient(data)
        })

        this.socket.on('RECEIVE_MESSAGE', function(data){
            addMessage(data);
        });

        this.socket.on('client', function(data, url) {
            addId(data);
        })

        const addMessage = data => {
            console.log(data);
            this.setState({messages: [...this.state.messages, data]});
            console.log(this.state.messages);
        };

        const addId = (data) => {
            axios.get(`http://18.191.254.197/getprofile${data.url}`)
            .then((user) => {
                let username = user.data.firstName + ' ' + user.data.lastName
                let id = data.id

                let client = {
                    id: id,
                    username: username
                }

                this.setState({id: [...this.state.id, client]}, function() {
                    console.log(this.state.id)
                })
                this.setState({state: this.state})
            })
        }

        const removeClient = data => {
            this.state.id.map((client) => {
                if(client.id === data) {
                    this.state.id.splice(client, 1)
                };
            })
            this.setState({ state: this.state });
        }

        this.sendMessage = ev => {
            ev.preventDefault();
            this.socket.emit('SEND_MESSAGE', {
                recipient: this.state.recipient,
                author: this.state.username,
                message: this.state.message
            })
            this.setState({message: ''});
        }

        this.handleChange = panel => (event, expanded) => {
            this.setState({
              expanded: expanded ? panel : false,
            });
        };
    }

    render() {
        return (      
            <div>
            <Paper>
                {this.state.id.map((client, i) => {
                    return(     
                    <div key={client.id} className='chat-container' onClick={()=> {this.setState({recipient: client.id})}}>
                    <ExpansionPanel expanded={this.state.expanded === i} onChange={this.handleChange(i)}>
                        <ExpansionPanelSummary>
                        <Typography>{client.username}</Typography>
                        </ExpansionPanelSummary>
                        <ExpansionPanelDetails>
                            <Typography>
                                <div key={client.id} className="messages">
                                    {this.state.messages.map(message => {
                                        if(client.id === this.state.id) {
                                            return (
                                                <div>{message.author}: {message.message}</div>
                                            )
                                        }
                                    })}
                                </div>
                                <div className="card-footer">
                                    <TextField 
                                        id="outlined-bare" 
                                        margin="normal" 
                                        variant="outlined" 
                                        placeholder="Message" 
                                        value={this.state.message} 
                                        onChange={ev => this.setState({message: ev.target.value})}/>
                                    <br/>
                                <Button onClick={this.sendMessage} variant="outlined" color="primary">Send</Button>
                                </div>
                        </Typography>
                    </ExpansionPanelDetails>
                  </ExpansionPanel>
                  </div>
                )})}
            </Paper>
            </div>
        );
    }
}

export default Chat;
